mapper.select:
select si_seq, department_name, si_number, si_name, si_reg_dt, si_mod_dt, si_status, si_phone_num, si_email, si_birth from ( SELECT a.*, b.di_name as department_name FROM student_info a inner join department_info b on a.si_di_seq = b.di_seq ) c
where department_name like #{keyword}
where si_name like #{keyword}
order by si_seq desc limit 10 offset #{offset}

select count(*) from ( SELECT a.*, b.di_name as department_name FROM student_info a inner join department_info b on a.si_di_seq = b.di_seq ) c
where department_name like #{keyword}
where si_name like #{keyword}

mapper.update:
UPDATE student_info
SET si_di_seq = #{si_di_seq}, si_number = #{si_number}, si_pwd = #{si_pwd}, si_name = #{si_name},  si_birth = #{si_birth}, si_phone_num=#{si_phone_num}, si_email=#{si_email}, si_status = #{si_status}, si_mod_dt = now()
WHERE si_seq = #{si_seq};
